sys_variable_value.value:
(function execute(inputs, outputs) {
  var loop_array = [];
  for(var i = 0; i < inputs.count; i++) {
    loop_array.push(i+1);
  }
  outputs.loop_array = loop_array;

})(inputs, outputs);

